        <div class="xl:col-span-2">
            <x-bar-chart :yearlyViews="$yearlyViews" :totalViewsIn5Years="$totalViewsIn5Years" :yearlyContacts="$yearlyContacts" />
        </div>
        <div class="xl:col-span-2">
            <x-pizza-chart :pizzaData="$pizzaData" />
        </div>
    </div>

